import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import AuthService from "../services/auth.service";

const NavBar = () => {
  const [currentUser, setCurrentUser] = useState(undefined);
  const navigate = useNavigate();

  useEffect(() => {
    const user = AuthService.getCurrentUser();

    if (user) {
      setCurrentUser(user);
    }
  }, []);

  const logOut = () => {
    AuthService.logout();
    setCurrentUser(undefined);
    navigate("/");
    window.location.reload();
  };

  return (
    <Navbar bg="light" expand="lg" className="px-3">
      <Container fluid>
        <Navbar.Brand as={Link} to="/">
          <i className="fas fa-dumbbell me-2"></i>
          CALI<span style={{ color: "#ff5733" }}>STRENGTH</span>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/">
              Home
            </Nav.Link>
            <Nav.Link as={Link} to="/workouts">
              Workouts
            </Nav.Link>
            <Nav.Link as={Link} to="/exercises">
              Exercises
            </Nav.Link>
            <Nav.Link as={Link} to="/progress">
              Track Progress
            </Nav.Link>
          </Nav>

          {currentUser ? (
            <Nav className="d-flex align-items-center">
              <Nav.Link as={Link} to="/profile" className="d-flex align-items-center">
                <div className="avatar-mini me-2">
                  <i className="fas fa-user"></i>
                </div>
                {currentUser.username}
              </Nav.Link>
              <Button variant="outline-primary" className="ms-2" onClick={logOut}>
                <i className="fas fa-sign-out-alt me-1"></i> Log Out
              </Button>
            </Nav>
          ) : (
            <Nav className="d-flex align-items-center">
              <Nav.Link as={Link} to="/login" className="me-2">
                Login
              </Nav.Link>
              <Button as={Link} to="/register" variant="primary">
                Sign Up Free
              </Button>
            </Nav>
          )}
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default NavBar;
