import React, { useState, useEffect } from "react";
import { Container, Row, Col, Card, ListGroup, Button, Spinner, Alert } from "react-bootstrap";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";
import UploadService from "../services/upload.service";
import FileUploader from "./FileUploader";
import "./UploadPage.css";

const UploadPage = () => {
  const [redirect, setRedirect] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [uploads, setUploads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [deleteLoading, setDeleteLoading] = useState(false);

  useEffect(() => {
    // Check if user is logged in
    const user = AuthService.getCurrentUser();
    if (!user) {
      setRedirect("/login");
    } else {
      setCurrentUser(user);
      loadUploads();
    }
  }, []);

  const loadUploads = () => {
    setLoading(true);
    setError("");
    
    UploadService.getMyUploads()
      .then(response => {
        setUploads(response.data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error loading uploads:", err);
        setError("Failed to load your uploads. Please try again.");
        setLoading(false);
      });
  };

  const handleUploadSuccess = (data) => {
    setSuccessMessage("Files uploaded successfully!");
    // Reload uploads to show the new upload
    loadUploads();
    
    // Clear success message after 3 seconds
    setTimeout(() => {
      setSuccessMessage("");
    }, 3000);
  };

  const handleDelete = (postId) => {
    setDeleteLoading(true);
    setError("");
    
    UploadService.deletePost(postId)
      .then(() => {
        setUploads(uploads.filter(upload => upload.id !== postId));
        setSuccessMessage("Post deleted successfully!");
        
        // Clear success message after 3 seconds
        setTimeout(() => {
          setSuccessMessage("");
        }, 3000);
      })
      .catch(err => {
        console.error("Error deleting post:", err);
        setError("Failed to delete the post. Please try again.");
      })
      .finally(() => {
        setDeleteLoading(false);
      });
  };

  if (redirect) {
    return <Navigate to={redirect} />;
  }

  return (
    <Container className="mt-4 upload-page">
      <h2 className="mb-4">Upload Files</h2>
      
      {error && <Alert variant="danger">{error}</Alert>}
      {successMessage && <Alert variant="success">{successMessage}</Alert>}
      
      <Row>
        <Col lg={8}>
          <FileUploader onUploadSuccess={handleUploadSuccess} />
        </Col>
        
        <Col lg={4}>
          <Card className="my-uploads-card">
            <Card.Header as="h5">My Uploads</Card.Header>
            <Card.Body>
              {loading ? (
                <div className="text-center my-3">
                  <Spinner animation="border" role="status">
                    <span className="visually-hidden">Loading...</span>
                  </Spinner>
                </div>
              ) : uploads.length === 0 ? (
                <Alert variant="info">You haven't uploaded any files yet.</Alert>
              ) : (
                <ListGroup variant="flush">
                  {uploads.map(upload => (
                    <ListGroup.Item key={upload.id} className="upload-item">
                      <div className="d-flex justify-content-between align-items-start">
                        <div>
                          <p className="mb-1 upload-text">{upload.text || "(No description)"}</p>
                          <small className="text-muted">
                            {new Date(upload.createdAt).toLocaleString()}
                          </small>
                          <div className="upload-details mt-1">
                            {upload.images && upload.images.length > 0 && (
                              <span className="badge bg-primary me-2">
                                <i className="fas fa-image me-1"></i> 
                                {upload.images.length} {upload.images.length === 1 ? "Image" : "Images"}
                              </span>
                            )}
                            {upload.video && (
                              <span className="badge bg-danger">
                                <i className="fas fa-video me-1"></i> Video
                              </span>
                            )}
                          </div>
                        </div>
                        <Button 
                          variant="outline-danger" 
                          size="sm"
                          onClick={() => handleDelete(upload.id)}
                          disabled={deleteLoading}
                        >
                          <i className="fas fa-trash"></i>
                        </Button>
                      </div>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default UploadPage;
