import React, { useState } from "react";
import { Container, Card, Form, Button, Row, Col, Spinner, Alert } from "react-bootstrap";
import axios from "axios";
import authHeader from "../services/auth-header";
import "./FileUploader.css";

const FileUploader = ({ onUploadSuccess }) => {
  const [images, setImages] = useState([]);
  const [video, setVideo] = useState(null);
  const [imagePreviews, setImagePreviews] = useState([]);
  const [videoPreview, setVideoPreview] = useState(null);
  const [description, setDescription] = useState("");
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    
    // Validate number of images (max 3)
    if (files.length > 3) {
      setError("You can upload a maximum of 3 images");
      return;
    }

    // Clear existing previews
    imagePreviews.forEach(url => URL.revokeObjectURL(url));
    
    setImages(files);
    
    // Create preview URLs
    const previews = files.map(file => URL.createObjectURL(file));
    setImagePreviews(previews);
    setError("");
  };

  const handleVideoChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    
    // Clear existing video preview
    if (videoPreview) URL.revokeObjectURL(videoPreview);
    
    // Create a video element to check duration
    const videoElement = document.createElement('video');
    videoElement.preload = 'metadata';
    
    videoElement.onloadedmetadata = function() {
      window.URL.revokeObjectURL(videoElement.src);
      
      // Check if video is less than 60 seconds
      if (videoElement.duration > 60) {
        setError("Video must be 60 seconds or less");
        setVideo(null);
        setVideoPreview(null);
      } else {
        setVideo(file);
        setVideoPreview(URL.createObjectURL(file));
        setError("");
      }
    };
    
    videoElement.src = URL.createObjectURL(file);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const removeImage = (index) => {
    // Revoke the URL to prevent memory leaks
    URL.revokeObjectURL(imagePreviews[index]);
    
    // Remove the image from state
    setImagePreviews(imagePreviews.filter((_, i) => i !== index));
    setImages(images.filter((_, i) => i !== index));
  };

  const removeVideo = () => {
    // Revoke the URL to prevent memory leaks
    if (videoPreview) URL.revokeObjectURL(videoPreview);
    
    // Remove the video from state
    setVideoPreview(null);
    setVideo(null);
  };

  const resetForm = () => {
    // Clear previews first
    imagePreviews.forEach(url => URL.revokeObjectURL(url));
    if (videoPreview) URL.revokeObjectURL(videoPreview);
    
    // Reset state
    setImages([]);
    setVideo(null);
    setImagePreviews([]);
    setVideoPreview(null);
    setDescription("");
    setError("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation
    if (images.length === 0 && !video) {
      setError("Please select at least one image or video");
      return;
    }
    
    setUploading(true);
    setError("");
    setSuccess("");
    
    try {
      const formData = new FormData();
      
      // Add description
      formData.append("text", description);
      
      // Add images
      images.forEach((image) => {
        formData.append("images", image);
      });
      
      // Add video
      if (video) {
        formData.append("video", video);
      }
      
      // Send the request
      const response = await axios.post("/api/posts", formData, {
        headers: {
          ...authHeader(),
          "Content-Type": "multipart/form-data"
        }
      });
      
      setSuccess("Files uploaded successfully!");
      resetForm();
      
      // Call onUploadSuccess if provided
      if (onUploadSuccess) {
        onUploadSuccess(response.data);
      }
    } catch (err) {
      console.error("Upload error:", err);
      setError(err.response?.data?.message || "Failed to upload files. Please try again.");
    } finally {
      setUploading(false);
    }
  };

  return (
    <Container className="mt-4">
      <Card className="file-uploader-card">
        <Card.Header as="h5">Upload Files</Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}
          {success && <Alert variant="success">{success}</Alert>}
          
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={description}
                onChange={handleDescriptionChange}
                placeholder="Add a description for your files..."
              />
            </Form.Group>
            
            <Row className="mb-3">
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Images (Max 3)</Form.Label>
                  <Form.Control
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={handleImageChange}
                    disabled={uploading}
                  />
                  <Form.Text className="text-muted">
                    Select up to 3 images (.jpg, .png, .gif)
                  </Form.Text>
                </Form.Group>
              </Col>
              
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Video (Optional)</Form.Label>
                  <Form.Control
                    type="file"
                    accept="video/*"
                    onChange={handleVideoChange}
                    disabled={uploading}
                  />
                  <Form.Text className="text-muted">
                    Select a video file (max 60 seconds)
                  </Form.Text>
                </Form.Group>
              </Col>
            </Row>
            
            {/* Image Previews */}
            {imagePreviews.length > 0 && (
              <div className="mb-3">
                <label>Image Previews:</label>
                <Row>
                  {imagePreviews.map((src, index) => (
                    <Col md={4} key={index} className="preview-item">
                      <div className="preview-container">
                        <img 
                          src={src} 
                          alt={`Preview ${index}`} 
                          className="img-preview" 
                        />
                        <Button 
                          variant="danger" 
                          size="sm" 
                          className="remove-btn"
                          onClick={() => removeImage(index)}
                          disabled={uploading}
                        >
                          <i className="fas fa-times"></i>
                        </Button>
                      </div>
                    </Col>
                  ))}
                </Row>
              </div>
            )}
            
            {/* Video Preview */}
            {videoPreview && (
              <div className="mb-3">
                <label>Video Preview:</label>
                <div className="preview-container">
                  <video 
                    src={videoPreview} 
                    controls 
                    className="video-preview" 
                  />
                  <Button 
                    variant="danger" 
                    size="sm" 
                    className="remove-btn"
                    onClick={removeVideo}
                    disabled={uploading}
                  >
                    <i className="fas fa-times"></i>
                  </Button>
                </div>
              </div>
            )}
            
            <div className="d-flex justify-content-end">
              <Button 
                variant="secondary" 
                className="me-2" 
                onClick={resetForm}
                disabled={uploading}
              >
                Clear
              </Button>
              <Button 
                variant="primary" 
                type="submit"
                disabled={uploading || (images.length === 0 && !video)}
              >
                {uploading ? (
                  <>
                    <Spinner
                      as="span"
                      animation="border"
                      size="sm"
                      role="status"
                      aria-hidden="true"
                      className="me-2"
                    />
                    Uploading...
                  </>
                ) : (
                  "Upload Files"
                )}
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default FileUploader;
