import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import AuthService from "../services/auth.service";

const NavBar = () => {
  const [currentUser, setCurrentUser] = useState(undefined);
  const navigate = useNavigate();

  useEffect(() => {
    const user = AuthService.getCurrentUser();

    if (user) {
      setCurrentUser(user);
    }
  }, []);

  const logOut = () => {
    AuthService.logout();
    setCurrentUser(undefined);
    navigate("/");
    window.location.reload();
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg" className="px-3">
      <Container fluid>
        <Navbar.Brand as={Link} to={currentUser ? "/wall" : "/"}>
          CALISTHENICFLOW
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            {currentUser && (
              <>
                <Nav.Link as={Link} to="/wall">
                  <i className="fas fa-home me-1"></i> Feed
                </Nav.Link>
                <Nav.Link as={Link} to="/profile">
                  <i className="fas fa-user me-1"></i> Profile
                </Nav.Link>
              </>
            )}
          </Nav>

          {currentUser ? (
            <Nav>
              <span className="navbar-text me-3 d-none d-md-block">
                Welcome, {currentUser.username}
              </span>
              <Button variant="outline-light" onClick={logOut}>
                <i className="fas fa-sign-out-alt me-1"></i> Log Out
              </Button>
            </Nav>
          ) : (
            <Nav>
              <Nav.Link as={Link} to="/login">
                <i className="fas fa-sign-in-alt me-1"></i> Login
              </Nav.Link>
              <Nav.Link as={Link} to="/register">
                <i className="fas fa-user-plus me-1"></i> Sign Up
              </Nav.Link>
            </Nav>
          )}
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default NavBar;
